properties.widgets.SqlServer-batchsink:
{
  "outputs": [],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this sink for lineage"}
        },
        {
          "widget-type": "textbox",
          "name": "jdbcPluginName",
          "label": "Driver Name",
          "widget-attributes": {"default": "sqlserver42"}
        },
        {
          "widget-type": "textbox",
          "name": "host",
          "label": "Host",
          "widget-attributes": {"default": "localhost"}
        },
        {
          "widget-type": "number",
          "name": "port",
          "label": "Port",
          "widget-attributes": {"default": "1433"}
        },
        {
          "widget-type": "textbox",
          "name": "database",
          "label": "Database"
        },
        {
          "widget-type": "textbox",
          "name": "tableName",
          "label": "Table Name"
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "name": "authentication",
          "widget-type": "radio-group",
          "label": "Authentication Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "SQL Login",
            "options": [
              {
                "id": "SQL Login",
                "label": "SQL Login"
              },
              {
                "id": "ActiveDirectoryPassword",
                "label": "Active Directory Password"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "user",
          "label": "Username"
        },
        {
          "widget-type": "password",
          "name": "password",
          "label": "Password"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "instanceName",
          "label": "Instance Name"
        },
        {
          "widget-type": "number",
          "name": "queryTimeout",
          "label": "Query Timeout",
          "widget-attributes": {"default": "-1"}
        },
        {
          "widget-type": "number",
          "name": "loginTimeout",
          "label": "Connect Timeout",
          "widget-attributes": {"default": "0"}
        },
        {
          "widget-type": "toggle",
          "name": "columnEncryptionSetting",
          "label": "Column Encryption",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "Disabled",
              "value": "false"
            },
            "on": {
              "label": "Enabled",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "toggle",
          "name": "encrypt",
          "label": "Encrypt",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "No",
              "value": "false"
            },
            "on": {
              "label": "Yes",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "toggle",
          "name": "trustServerCertificate",
          "label": "Trust Server Certificate",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "No",
              "value": "false"
            },
            "on": {
              "label": "Yes",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "textbox",
          "name": "workstationID",
          "label": "Workstation ID"
        },
        {
          "widget-type": "textbox",
          "name": "failoverPartner",
          "label": "Failover Partner"
        },
        {
          "widget-type": "number",
          "name": "packetSize",
          "label": "Packet Size",
          "widget-attributes": {"default": "-1"}
        },
        {
          "widget-type": "textbox",
          "name": "currentLanguage",
          "label": "Current Language"
        },
        {
          "widget-type": "keyvalue",
          "name": "connectionArguments",
          "label": "Connection Arguments",
          "widget-attributes": {
            "kv-delimiter": "=",
            "key-placeholder": "Key",
            "value-placeholder": "Value",
            "delimiter": ";",
            "showDelimiter": "false"
          }
        }
      ]
    }
  ],
  "display-name": "SQL Server",
  "icon": {
