properties.widgets.SqlServer-postaction:
{
  "metadata": {"spec-version": "1.0"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "select",
          "name": "runCondition",
          "label": "Run Condition",
          "widget-attributes": {
            "default": "success",
            "values": [
              "completion",
              "success",
              "failure"
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "jdbcPluginName",
          "label": "Driver Name",
          "widget-attributes": {"default": "sqlserver42"}
        },
        {
          "widget-type": "textbox",
          "name": "host",
          "label": "Host",
          "widget-attributes": {"default": "localhost"}
        },
        {
          "widget-type": "number",
          "name": "port",
          "label": "Port",
          "widget-attributes": {"default": "1433"}
        },
        {
          "widget-type": "textbox",
          "name": "database",
          "label": "Database"
        },
        {
          "widget-type": "textarea",
          "name": "query",
          "label": "Query",
          "widget-attributes": {"rows": "4"}
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "name": "authentication",
          "widget-type": "radio-group",
          "label": "Authentication Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "SQL Login",
            "options": [
              {
                "id": "SQL Login",
                "label": "SQL Login"
              },
              {
                "id": "ActiveDirectoryPassword",
                "label": "Active Directory Password"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "user",
          "label": "Username"
        },
        {
          "widget-type": "password",
          "name": "password",
          "label": "Password"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "instanceName",
          "label": "Instance Name"
        },
        {
          "widget-type": "number",
          "name": "queryTimeout",
          "label": "Query Timeout",
          "widget-attributes": {"default": "-1"}
        },
        {
          "name": "applicationIntent",
          "widget-type": "radio-group",
          "label": "Application Intent",
          "widget-attributes": {
            "layout": "inline",
            "default": "ReadWrite",
            "options": [
              {
                "id": "ReadWrite",
                "label": "ReadWrite"
              },
              {
                "id": "ReadOnly",
                "label": "ReadOnly"
              }
            ]
          }
        },
        {
          "widget-type": "number",
          "name": "loginTimeout",
          "label": "Connect Timeout",
          "widget-attributes": {"default": "0"}
        },
        {
          "widget-type": "toggle",
          "name": "columnEncryptionSetting",
          "label": "Column Encryption",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "Disabled",
              "value": "false"
            },
            "on": {
              "label": "Enabled",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "toggle",
          "name": "encrypt",
          "label": "Encrypt",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "No",
              "value": "false"
            },
            "on": {
              "label": "Yes",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "toggle",
          "name": "trustServerCertificate",
          "label": "Trust Server Certificate",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "No",
              "value": "false"
            },
            "on": {
              "label": "Yes",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "textbox",
          "name": "workstationID",
          "label": "Workstation ID"
        },
        {
          "widget-type": "textbox",
          "name": "failoverPartner",
          "label": "Failover Partner"
        },
        {
          "widget-type": "number",
          "name": "packetSize",
          "label": "Packet Size",
          "widget-attributes": {"default": "-1"}
        },
        {
          "widget-type": "textbox",
          "name": "currentLanguage",
          "label": "Current Language"
