properties.widgets.Postgres-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "widget-attributes": {
      "schema-types": [
        "boolean",
        "int",
        "long",
        "float",
        "double",
        "bytes",
        "string"
      ],
      "schema-default-type": "string"
    }
  }],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this source for lineage"}
        },
        {
          "widget-type": "textbox",
          "name": "jdbcPluginName",
          "label": "Driver Name",
          "widget-attributes": {"default": "postgresql"}
        },
        {
          "widget-type": "textbox",
          "name": "host",
          "label": "Host",
          "widget-attributes": {"default": "localhost"}
        },
        {
          "widget-type": "number",
          "name": "port",
          "label": "Port",
          "widget-attributes": {"default": "5432"}
        },
        {
          "widget-type": "textbox",
          "name": "database",
          "label": "Database"
        },
        {
          "widget-type": "textarea",
          "name": "importQuery",
          "label": "Import Query",
          "widget-attributes": {"rows": "4"},
          "plugin-function": {
            "widget": "getSchema",
            "method": "POST",
            "output-property": "schema",
            "plugin-method": "getSchema"
          }
        },
        {
          "widget-type": "textarea",
          "name": "boundingQuery",
          "label": "Bounding Query",
          "widget-attributes": {"rows": "4"}
        },
        {
          "widget-type": "textbox",
          "name": "splitBy",
          "label": "Split-By Field Name"
        },
        {
          "widget-type": "textbox",
          "name": "numSplits",
          "label": "Number of Splits to Generate"
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "user",
          "label": "Username"
        },
        {
          "widget-type": "password",
          "name": "password",
          "label": "Password"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "keyvalue",
          "name": "connectionArguments",
          "label": "Connection Arguments",
          "widget-attributes": {
            "kv-delimiter": "=",
            "key-placeholder": "Key",
            "value-placeholder": "Value",
            "delimiter": ";",
            "showDelimiter": "false"
          }
        },
        {
          "widget-type": "number",
          "name": "connectionTimeout",
          "label": "Connection Timeout",
          "widget-attributes": {"default": "100"}
        }
      ]
    }
  ],
  "display-name": "PostgreSQL",
