properties.widgets.Database-action:
{
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "jdbcPluginName",
          "label": "Plugin Name"
        },
        {
          "widget-type": "textbox",
          "name": "jdbcPluginType",
          "label": "Plugin Type",
          "widget-attributes": {"default": "jdbc"}
        },
        {
          "widget-type": "textbox",
          "name": "connectionString",
          "label": "Connection String"
        },
        {
          "widget-type": "textarea",
