          "widget-type": "textbox",
          "name": "connectionString",
          "label": "Connection String"
        },
        {
          "widget-type": "textbox",
          "name": "tableName",
          "label": "Table Name"
        },
        {
          "widget-type": "csv",
          "name": "columns",
          "label": "Columns",
          "widget-attributes": {"delimiter": ","}
        }
      ]
