    {
      "label": "Credentials",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "user",
          "label": "Username"
        },
        {
          "widget-type": "password",
          "name": "password",
          "label": "Password"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "keyvalue",
          "name": "connectionArguments",
          "label": "Connection Arguments",
          "widget-attributes": {
            "kv-delimiter": "=",
            "key-placeholder": "Key",
            "value-placeholder": "Value",
            "delimiter": ";",
            "showDelimiter": "false"
          }
        },
        {
          "widget-type": "radio-group",
          "name": "enableAutoCommit",
          "label": "Enable Auto-Commit",
