properties.widgets.Database-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "widget-attributes": {
      "schema-types": [
        "boolean",
        "int",
        "long",
        "float",
        "double",
        "bytes",
        "string"
      ],
      "schema-default-type": "string"
    }
  }],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Connection",
      "properties": [
        {
          "widget-type": "hidden",
          "name": "useConnection",
          "widget-attributes": {"default": "false"}
        },
        {
          "widget-type": "connection-select",
          "name": "connection",
          "label": "Connection",
          "widget-attributes": {"connectionType": "Database"}
        },
        {
          "widget-type": "plugin-list",
          "name": "jdbcPluginName",
          "label": "JDBC Driver name",
          "widget-attributes": {"plugin-type": "jdbc"}
        },
        {
          "widget-type": "textbox",
          "name": "connectionString",
          "label": "Connection String"
        },
        {
          "widget-type": "textbox",
          "name": "user",
          "label": "Username"
        },
        {
          "widget-type": "password",
          "name": "password",
          "label": "Password"
        },
        {
          "widget-type": "keyvalue",
          "name": "connectionArguments",
          "label": "Connection Arguments",
          "widget-attributes": {
            "kv-delimiter": "=",
            "key-placeholder": "Key",
            "value-placeholder": "Value",
            "delimiter": ";",
            "showDelimiter": "false"
          }
        }
      ]
    },
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this source for lineage"}
        },
        {
          "widget-type": "hidden",
          "name": "jdbcPluginType",
          "widget-attributes": {"default": "jdbc"}
        },
        {
          "widget-type": "hidden",
          "name": "query"
        },
        {
          "widget-type": "connection-browser",
          "widget-category": "plugin",
          "widget-attributes": {
            "label": "Browse Database",
            "connectionType": "DATABASE"
          }
        },
        {
          "widget-type": "get-schema",
          "widget-category": "plugin"
        },
        {
          "widget-type": "textarea",
          "name": "importQuery",
          "label": "Import Query",
          "widget-attributes": {"rows": "4"}
        },
        {
          "widget-type": "textarea",
          "name": "boundingQuery",
          "label": "Bounding Query",
          "widget-attributes": {"rows": "4"}
        },
        {
          "widget-type": "textbox",
          "name": "splitBy",
          "label": "Split-By Field Name"
        },
        {
          "widget-type": "textbox",
          "name": "numSplits",
          "label": "Number of Splits to Generate",
          "widget-attributes": {"default": "1"}
        },
        {
          "widget-type": "textbox",
          "name": "fetchSize",
          "label": "Fetch Size",
          "widget-attributes": {"default": "1000"}
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "radio-group",
          "name": "enableAutoCommit",
          "label": "Enable Auto-Commit",
          "widget-attributes": {
            "layout": "inline",
            "default": "false",
            "options": [
              {
                "id": "true",
                "label": "True"
              },
              {
                "id": "false",
                "label": "False"
              }
            ]
          }
        },
        {
          "widget-type": "select",
          "name": "columnNameCase",
          "label": "Column Name Case",
          "widget-attributes": {
            "default": "No change",
            "values": [
              "UPPER",
              "lower",
              "No change"
            ]
          }
        },
        {
          "widget-type": "select",
          "name": "transactionIsolationLevel",
          "label": "Transaction Isolation Level",
          "widget-attributes": {
            "default": "TRANSACTION_SERIALIZABLE",
            "values": [
              "TRANSACTION_NONE",
              "TRANSACTION_READ_UNCOMMITTED",
              "TRANSACTION_READ_COMMITTED",
              "TRANSACTION_REPEATABLE_READ",
              "TRANSACTION_SERIALIZABLE"
            ]
          }
        }
      ]
    },
    {
      "label": "Column Names Normalization",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "patternToReplace",
          "label": "Pattern To Replace"
        },
        {
          "widget-type": "textbox",
          "name": "replaceWith",
          "label": "Replace With"
        }
      ]
    }
  ],
  "display-name": "Database",
  "filters": [
    {
      "condition": {"expression": "useConnection == false"},
      "name": "showConnectionProperties ",
      "show": [
        {
          "name": "jdbcPluginName",
          "type": "property"
        },
        {
          "name": "user",
          "type": "property"
        },
        {
          "name": "password",
          "type": "property"
        },
        {
          "name": "connectionString",
          "type": "property"
        },
        {
          "name": "connectionArguments",
          "type": "property"
        }
      ]
    },
    {
      "condition": {"expression": "useConnection == true"},
      "name": "showConnectionId",
      "show": [{
        "name": "connection",
        "type": "property"
      }]
    }
  ]
}
