properties.widgets.BigQueryTable-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "label": "schema",
    "widget-attributes": {
      "schema-types": [
        "boolean",
        "long",
        "double",
        "bytes",
        "string",
        "array"
      ],
      "schema-default-type": "string"
    }
  }],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this source for lineage"}
        },
        {
          "widget-type": "connection-browser",
          "widget-category": "plugin",
          "widget-attributes": {
            "label": "Browse",
            "connectionType": "BIGQUERY"
          }
        },
        {
          "widget-type": "textbox",
          "name": "project",
          "label": "Project ID",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "datasetProject",
          "label": "Dataset Project ID",
          "widget-attributes": {"placeholder": "Project the dataset belongs to, if different from the Project ID."}
        },
        {
          "widget-type": "textbox",
          "name": "dataset",
          "label": "Dataset",
          "widget-attributes": {"placeholder": "Dataset the table belongs to"}
        },
        {
          "widget-type": "textbox",
          "name": "table",
          "label": "Table",
          "widget-attributes": {"placeholder": "Table to read from"},
          "plugin-function": {
            "widget": "outputSchema",
            "output-property": "schema",
            "omit-properties": [{"name": "schema"}],
            "label": "Get Schema"
          }
        },
        {
          "widget-type": "textbox",
          "name": "partitionFrom",
          "label": "Partition Start Date",
          "widget-attributes": {"placeholder": "Partition start date in format yyyy-MM-dd"}
        },
        {
          "widget-type": "textbox",
          "name": "partitionTo",
          "label": "Partition End Date",
          "widget-attributes": {"placeholder": "Partition end date in format yyyy-MM-dd"}
        },
        {
          "widget-type": "textbox",
          "name": "filter",
          "label": "Filter",
          "widget-attributes": {"placeholder": ""}
        },
        {
          "widget-type": "textbox",
          "name": "bucket",
          "label": "Temporary Bucket Name",
          "widget-attributes": {"placeholder": "Google Cloud Storage bucket for temporary data"}
        }
      ]
    },
    {
      "label": "Views",
      "properties": [
        {
          "widget-type": "toggle",
          "name": "enableQueryingViews",
          "label": "Enable querying views",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "NO",
              "value": "false"
            },
            "on": {
              "label": "YES",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "textbox",
          "name": "viewMaterializationProject",
          "label": "View Materialization Project"
        },
        {
          "widget-type": "textbox",
          "name": "viewMaterializationDataset",
          "label": "View Materialization Dataset"
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "name": "serviceAccountType",
          "widget-type": "radio-group",
          "label": "Service Account Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "filePath",
            "options": [
              {
                "id": "filePath",
                "label": "File Path"
              },
              {
                "id": "JSON",
                "label": "JSON"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "serviceFilePath",
          "label": "Service Account File Path",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "serviceAccountJSON",
          "label": "Service Account JSON"
        }
      ]
    }
  ],
  "display-name": "BigQuery",
  "icon": {
    "arguments": {"data": "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAADIAAAAyCAYAAAAeP4ixAAAAAXNSR0IArs4c6QAAAAlwSFlzAAAL\r\nEwAACxMBAJqcGAAAActpVFh0WE1MOmNvbS5hZG9iZS54bXAAAAAAADx4OnhtcG1ldGEgeG1sbnM6\r\neD0iYWRvYmU6bnM6bWV0YS8iIHg6eG1wdGs9IlhNUCBDb3JlIDUuNC4wIj4KICAgPHJkZjpSREYg\r\neG1sbnM6cmRmPSJodHRwOi8vd3d3LnczLm9yZy8xOTk5LzAyLzIyLXJkZi1zeW50YXgtbnMjIj4K\r\nICAgICAgPHJkZjpEZXNjcmlwdGlvbiByZGY6YWJvdXQ9IiIKICAgICAgICAgICAgeG1sbnM6eG1w\r\nPSJodHRwOi8vbnMuYWRvYmUuY29tL3hhcC8xLjAvIgogICAgICAgICAgICB4bWxuczp0aWZmPSJo\r\ndHRwOi8vbnMuYWRvYmUuY29tL3RpZmYvMS4wLyI+CiAgICAgICAgIDx4bXA6Q3JlYXRvclRvb2w+\r\nQWRvYmUgSW1hZ2VSZWFkeTwveG1wOkNyZWF0b3JUb29sPgogICAgICAgICA8dGlmZjpPcmllbnRh\r\ndGlvbj4xPC90aWZmOk9yaWVudGF0aW9uPgogICAgICA8L3JkZjpEZXNjcmlwdGlvbj4KICAgPC9y\r\nZGY6UkRGPgo8L3g6eG1wbWV0YT4KKS7NPQAADlhJREFUaAW1WmmQXFUVPm/rZfZMMhMmZVgSliiC\r\nKZKgEoFJhgyKbJYmghSoyCJVVBGxBCsmMJANkc2SolAof0ChFAMFIktBtkGEChCEgEEWjUBJ9pnM\r\n1tPdb7t+3339Jj3TPTOdFLk1b97t++4953xnu+e+bkMOuimjtUOsgRYxDnppBQtqd4rq6pBAxFAV\r\nTD+0KYsXK0tEHRYAIyVSRsRr5Oh4nyoWioQ7Ow1oSqR9jZrlicwwJTAN01IqDCqmU06YmEYoVuiI\r\nbH9xmfE+5xXzLLeueKwiAWKCZ9+lGr18cI8hxncN06waJhRToTPE/eGHE3RGOZAKwyEl6gknaS19\r\n4XqjJ+Y9ARUxJ5rQ0aFMWmLO75Xju8HjqVrrUoKAGWT4CtHnxbG4X+m9mA76pE0e5EWe5E0ZJpJz\r\nwgldEoGd1BMuddLWgvxA4MGVAgNSx9cBRAekKn4W94vv8ZriMfZJmzzIizwJIJaB/bHauI7Q2qHs\r\nrg7DX7gqe5RYzhuWZTWBj4+At8ci+PmMG75pWbYKgr1h4M3buDz9SSzLWPTHsYgyukRCLjQsZxk0\r\nBBC+j6yIzHWYG3iQlwWe5E1uXVqWsTPmmEC4V0iHES5YMzQfbnBFkNNRaUgIYgr9w3mRB/RHnnC3\r\nK7QMkEXLRFRlWnkgCC66FOebYt9sp2xTQt+F8IffGrGQ5AWeFnibyr6Zw1qmMQK/LJDWQoC3rc1f\r\nYiecRUHOC2EDB6ZAduUVZdnDd4/4kCd520lnEWUhmFg29otbSbDHefv8X6vaId99w0omTghcl9Y5\r\nzAFeLNaIvm8lEnaQdz+oshPznr7RGIhlLJ5VYpG9J0Zb2lDoX2+nNYgAIwftUtAmbFd6FTOvqA/e\r\nUGRAWSgT18QyFq8fYZE4xbWtyR2PB6+bVrI+DPIsSyoGQuHZbBDgZRQ4cNxHDvQLEwrDem4F/wLI\r\ngkyW78PyUzcsS30YyxqvPeAuCunWiAIccbDcSafqvVweJZWg/KmsMZElABnBJANwxp2ukn5dnYnU\r\nWYZMTojUgqOHeS7r28rRWGGYxyaZrPeyueWQ5jId+JAZRLRqhl2r9ZZI6+1rswsNw7rUy7lMsRCL\r\n8yq70raS/RD+6W7cIe0pkw1ZfEx0nTJ