properties.widgets.BigQueryTable-batchsink:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "label": "schema",
    "widget-attributes": {
      "schema-types": [
        "boolean",
        "int",
        "long",
        "float",
        "double",
        "bytes",
        "string"
      ],
      "schema-default-type": "string"
    }
  }],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this sink for lineage"}
        },
        {
          "widget-type": "textbox",
          "name": "project",
          "label": "Project ID",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "datasetProject",
          "label": "Dataset Project Id",
          "widget-attributes": {"placeholder": "The project in which the dataset is located/should be created. Defaults to the project specified in the Project Id property."}
        },
        {
          "widget-type": "textbox",
          "name": "dataset",
          "label": "Dataset",
          "widget-attributes": {"placeholder": "Dataset the table belongs to"}
        },
        {
          "widget-type": "textbox",
          "name": "table",
          "label": "Table",
          "widget-attributes": {"placeholder": "Table to write to"}
        },
        {
          "widget-type": "textbox",
          "name": "bucket",
          "label": "Temporary Bucket Name",
          "widget-attributes": {"placeholder": "Google Cloud Storage bucket for temporary data"}
        },
        {
          "widget-type": "textbox",
          "name": "gcsChunkSize",
          "label": "GCS Upload Request Chunk Size",
          "widget-attributes": {"placeholder": "GCS upload request chunk size in bytes"}
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "name": "serviceAccountType",
          "widget-type": "radio-group",
          "label": "Service Account Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "filePath",
            "options": [
              {
                "id": "filePath",
                "label": "File Path"
              },
              {
                "id": "JSON",
                "label": "JSON"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "serviceFilePath",
          "label": "Service Account File Path",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "serviceAccountJSON",
          "label": "Service Account JSON"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "radio-group",
          "name": "operation",
          "label": "Operation",
          "widget-attributes": {
            "layout": "inline",
            "default": "insert",
            "options": [
              {
                "id": "insert",
                "label": "Insert"
              },
              {
                "id": "update",
                "label": "Update"
              },
              {
                "id": "upsert",
                "label": "Upsert"
              }
            ]
          }
        },
        {
          "name": "relationTableKey",
          "widget-type": "csv",
          "label": "Table Key",
          "widget-attributes": {}
        },
        {
          "name": "dedupeBy",
          "widget-type": "keyvalue-dropdown",
          "label": "Dedupe By",
          "widget-attributes": {
            "kv-delimiter": " ",
            "dropdownOptions": [
              "ASC",
              "DESC"
            ],
            "delimiter": ","
          }
        },
        {
          "widget-type": "textbox",
          "name": "partitionFilter",
          "label": "Partition Filter",
          "widget-attributes": {"placeholder": "Filter that can be used for partition elimination"}
        },
        {
          "widget-type": "toggle",
          "name": "truncateTable",
          "label": "Truncate Table",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "False",
              "value": "false"
            },
            "on": {
              "label": "True",
              "value": "true"
            }
          }
        },
        {
          "name": "allowSchemaRelaxation",
          "widget-type": "toggle",
          "label": "Update Table Schema",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "False",
              "value": "false"
            },
            "on": {
              "label": "True",
              "value": "true"
            }
          }
        }
      ]
    },
    {
      "label": "Auto Create",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "location",
          "label": "Location",
          "widget-attributes": {"default": "US"}
        },
        {
          "name": "createPartitionedTable",
          "widget-type": "toggle",
          "label": "Create Partitioned Table",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "False",
              "value": "false"
            },
            "on": {
              "label": "True",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "radio-group",
          "name": "partitioningType",
          "label": "Partitioning type",
          "widget-attributes": {
            "layout": "inline",
            "default": "TIME",
            "options": [
              {
                "id": "TIME",
                "label": "Time"
              },
              {
                "id": "INTEGER",
                "label": "Integer"
              },
              {
                "id": "NONE",
                "label": "None"
              }
            ]
          }
        },
        {
          "widget-type": "Number",
          "name": "rangeStart",
          "label": "Range Start (inclusive)",
          "widget-attributes": {"default": "0"}
        },
        {
          "widget-type": "Number",
          "name": "rangeEnd",
          "label": "Range End (exclusive)"
