properties.widgets.Spanner-batchsink:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "widget-attributes": {
      "schema-types": [
        "boolean",
        "long",
        "double",
        "string",
        "bytes"
      ],
      "schema-default-type": "string"
    }
  }],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this sink for lineage"}
        },
        {
          "widget-type": "textbox",
          "name": "project",
          "label": "Project ID",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "instance",
          "label": "Instance ID",
          "widget-attributes": {"placeholder": "Instance ID"}
        },
        {
          "widget-type": "textbox",
          "name": "database",
          "label": "Database Name",
          "widget-attributes": {"placeholder": "Database name"}
        },
        {
          "widget-type": "textbox",
          "name": "table",
          "label": "Table Name",
          "widget-attributes": {"placeholder": "Table name"}
        },
        {
          "widget-type": "csv",
          "name": "keys",
          "label": "Primary Key",
          "widget-attributes": {
            "value-placeholder": "Field Name",
            "delimiter": ","
          }
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "name": "serviceAccountType",
          "widget-type": "radio-group",
          "label": "Service Account Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "filePath",
            "options": [
              {
                "id": "filePath",
                "label": "File Path"
              },
              {
                "id": "JSON",
                "label": "JSON"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "serviceFilePath",
          "label": "Service Account File Path",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "serviceAccountJSON",
          "label": "Service Account JSON"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [{
        "widget-type": "textbox",
        "name": "batchSize",
        "label": "Write Batch Size",
        "widget-attributes": {"placeholder": "Maximum number of records to buffer in RecordWriter before writing to spanner table."}
      }]
    }
  ],
