properties.widgets.GoogleSubscriber-streamingsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "widget-attributes": {"default-schema": {
      "name": "etlSchemaBody",
      "type": "record",
      "fields": [
        {
          "name": "message",
          "type": "bytes"
        },
        {
          "name": "id",
          "type": "string"
        },
        {
          "name": "timestamp",
          "type": {
            "logicalType": "timestamp-micros",
            "type": "long"
          }
        },
        {
          "name": "attributes",
          "type": {
            "keys": "string",
            "values": "string",
            "type": "map"
          }
        }
      ]
    }}
  }],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this sink for lineage"}
        },
        {
          "widget-type": "textbox",
          "name": "project",
          "label": "Project ID",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "subscription",
          "label": "Subscription",
          "widget-attributes": {"placeholder": "Subscription to read from"}
        },
        {
          "widget-type": "textbox",
          "name": "topic",
          "label": "Topic",
          "widget-attributes": {"placeholder": "Topic to create the subscription from if it does not already exist"}
        },
        {
          "widget-type": "select",
          "name": "format",
          "label": "Format",
          "widget-attributes": {
            "default": "text",
            "values": [
              "avro",
              "blob",
              "tsv",
              "csv",
              "delimited",
              "json",
              "parquet",
              "text"
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "delimiter",
          "label": "Delimiter",
          "widget-attributes": {"placeholder": "Delimiter if the format is 'delimited'"}
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "name": "serviceAccountType",
          "widget-type": "radio-group",
          "label": "Service Account Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "filePath",
            "options": [
              {
                "id": "filePath",
                "label": "File Path"
              },
              {
                "id": "JSON",
                "label": "JSON"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "serviceFilePath",
          "label": "Service Account File Path",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "serviceAccountJSON",
          "label": "Service Account JSON"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [{
        "widget-type": "number",
        "name": "numberOfReaders",
        "label": "Number of Readers",
        "widget-attributes": {
          "default": "1",
          "min": "1"
        }
      }]
    }
  ],
  "display-name": "Pub/Sub",
  "icon": {
