  "filters": [
    {
      "condition": {"expression": "serviceAccountType == 'filePath'"},
      "name": "ServiceAuthenticationTypeFilePath",
      "show": [{
        "name": "serviceFilePath",
        "type": "property"
      }]
    },
    {
      "condition": {"expression": "serviceAccountType == 'JSON'"},
      "name": "ServiceAuthenticationTypeJSON",
      "show": [{
        "name": "serviceAccountJSON",
        "type": "property"
      }]
    },
    {
      "condition": {"expression": "contentType == 'other'"},
      "name": "CustomContentType",
      "show": [{
        "name": "customContentType",
        "type": "property"
      }]
    },
    {
      "condition": {
        "property": "format",
        "value": "delimited",
        "operator": "equal to"
      },
      "name": "delimiter",
      "show": [{"name": "delimiter"}]
    },
    {
      "condition": {"expression": "format == 'delimited' || format == 'csv' || format == 'tsv'"},
      "name": "header",
      "show": [{"name": "writeHeader"}]
    }
  ]
}

properties.widgets.GCS-batchsink:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "widget-attributes": {
      "schema-types": [
        "boolean",
        "int",
        "long",
        "float",
        "double",
        "bytes",
        "string"
      ],
      "schema-default-type": "string"
    }
  }],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this sink for lineage"}
        },
        {
          "widget-type": "textbox",
          "name": "project",
          "label": "Project ID",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "path",
          "label": "Path",
          "widget-attributes": {"placeholder": "gs://<bucket>/path/to/output"}
        },
        {
          "widget-type": "textbox",
          "name": "suffix",
          "label": "Path Suffix",
          "widget-attributes": {"default": "yyyy-MM-dd-HH-mm"}
        },
        {
          "widget-type": "plugin-list",
          "name": "format",
          "label": "Format",
          "widget-attributes": {"plugin-type": "validatingOutputFormat"}
        },
        {
          "widget-type": "textbox",
          "name": "delimiter",
          "label": "Delimiter",
          "widget-attributes": {"placeholder": "Delimiter if the format is 'delimited'"}
        },
        {
          "widget-type": "toggle",
          "name": "writeHeader",
          "label": "Write Header",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "False",
              "value": "false"
            },
            "on": {
              "label": "True",
              "value": "true"
            }
          }
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "name": "serviceAccountType",
          "widget-type": "radio-group",
          "label": "Service Account Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "filePath",
            "options": [
              {
                "id": "filePath",
                "label": "File Path"
              },
              {
                "id": "JSON",
                "label": "JSON"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "serviceFilePath",
          "label": "Service Account File Path",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "serviceAccountJSON",
          "label": "Service Account JSON"
        }
      ]
    },
    {
      "label": "Auto Create",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "location",
          "label": "Location",
          "widget-attributes": {"default": "us"}
        },
        {
          "widget-type": "select",
          "name": "contentType",
          "label": "Content Type",
          "widget-attributes": {
            "default": "application/octet-stream",
            "values": [
              "application/octet-stream",
              "application/json",
              "application/avro",
              "application/csv",
              "text/plain",
              "text/csv",
              "text/tab-separated-values",
              "other"
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "customContentType",
          "label": "Custom Content Type"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "outputFileNameBase",
          "label": "Output File Prefix"
        },
        {
          "widget-type": "json-editor",
          "name": "fileSystemProperties",
          "label": "File System Properties"
        }
      ]
    }
  ],
