  "filters": [
    {
      "condition": {"expression": "serviceAccountType == 'filePath'"},
      "name": "ServiceAuthenticationTypeFilePath",
      "show": [{
        "name": "serviceFilePath",
        "type": "property"
      }]
    },
    {
      "condition": {"expression": "serviceAccountType == 'JSON'"},
      "name": "ServiceAuthenticationTypeJSON",
      "show": [{
        "name": "serviceAccountJSON",
        "type": "property"
      }]
    },
    {
      "condition": {"expression": "contentType == 'other'"},
      "name": "CustomContentType",
      "show": [{
        "name": "customContentType",
        "type": "property"
      }]
    },
    {
      "condition": {
        "property": "format",
        "value": "delimited",
        "operator": "equal to"
      },
      "name": "delimiter",
      "show": [{"name": "delimiter"}]
    },
    {
      "condition": {"expression": "format == 'delimited' || format == 'csv' || format == 'tsv'"},
      "name": "header",
      "show": [{"name": "writeHeader"}]
    }
  ]
}

properties.widgets.Bigtable-batchsink:
{
  "outputs": [],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this source for lineage"}
        },
        {
          "widget-type": "textbox",
          "name": "project",
          "label": "Project ID",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "instance",
          "label": "Instance ID",
          "widget-attributes": {"placeholder": "Bigtable instance id"}
        },
        {
          "widget-type": "textbox",
          "name": "table",
          "label": "Table",
          "widget-attributes": {"placeholder": "Table to read from"}
        },
        {
          "widget-type": "textbox",
          "name": "keyAlias",
          "label": "Key Alias",
          "widget-attributes": {
            "default": "__key__",
            "placeholder": "Key alias"
          }
        },
        {
          "widget-type": "keyvalue",
          "name": "columnMappings",
          "label": "Column Mappings",
          "widget-attributes": {
            "kv-delimiter": "=",
            "delimiter": ",",
            "placeholder": "Mappings from record field to Bigtable column name. Column names must be formatted as <family>:<qualifier>."
          }
        },
        {
          "widget-type": "keyvalue",
          "name": "bigtableOptions",
          "label": "Bigtable Options",
          "widget-attributes": {
            "kv-delimiter": "=",
            "delimiter": ",",
            "placeholder": "Additional connection properties for Bigtable."
          }
        }
      ]
    },
