properties.widgets.GCSFile-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "widget-attributes": {"default-schema": {
      "name": "fileRecord",
      "type": "record",
      "fields": [
        {
          "name": "offset",
          "type": "long"
        },
        {
          "name": "body",
          "type": "string"
        }
      ]
    }}
  }],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this source for lineage"}
        },
        {
          "widget-type": "connection-browser",
          "widget-category": "plugin",
          "widget-attributes": {
            "label": "Browse",
            "connectionType": "GCS"
          }
        },
        {
          "widget-type": "textbox",
          "name": "project",
          "label": "Project ID",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "path",
          "label": "Path",
          "widget-attributes": {"placeholder": "gs://<bucket>/path/to/input"}
        },
        {
          "widget-type": "plugin-list",
          "name": "format",
          "label": "Format",
          "widget-attributes": {"plugin-type": "validatingInputFormat"}
        },
        {
          "widget-type": "number",
          "name": "sampleSize",
          "label": "Sample Size",
          "widget-attributes": {
            "default": "1000",
            "minimum": "1"
          }
        },
        {
          "widget-type": "keyvalue-dropdown",
          "name": "override",
          "label": "Override",
          "widget-attributes": {
            "dropdownOptions": [
              "boolean",
              "bytes",
              "double",
              "float",
              "int",
              "long",
              "string",
              "date",
              "time",
              "timestamp"
            ],
            "key-placeholder": "Field Name",
            "value-placeholder": "Data Type"
          }
        },
        {
          "widget-type": "textbox",
          "name": "delimiter",
          "label": "Delimiter",
          "widget-attributes": {"placeholder": "Delimiter if the format is 'delimited'"},
          "plugin-function": {
            "widget": "outputSchema",
            "missing-required-fields-message": "Please provide path field",
            "method": "POST",
            "required-fields": ["path"],
            "label": "Get Schema Value",
            "plugin-method": "getSchema"
          }
        },
        {
          "widget-type": "toggle",
          "name": "skipHeader",
          "label": "Skip Header",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "False",
              "value": "false"
            },
            "on": {
              "label": "True",
              "value": "true"
            }
          }
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "name": "serviceAccountType",
          "widget-type": "radio-group",
          "label": "Service Account Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "filePath",
            "options": [
              {
                "id": "filePath",
                "label": "File Path"
              },
              {
                "id": "JSON",
                "label": "JSON"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "serviceFilePath",
          "label": "Service Account File Path",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "serviceAccountJSON",
          "label": "Service Account JSON"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "minSplitSize",
          "label": "Minimum Split Size",
          "widget-attributes": {"placeholder": "Minimum split size for each partition specified in bytes"}
        },
        {
          "widget-type": "textbox",
          "name": "maxSplitSize",
          "label": "Maximum Split Size",
          "widget-attributes": {"placeholder": "Maximum split size for each partition specified in bytes"}
        },
        {
          "widget-type": "textbox",
          "name": "fileRegex",
          "label": "Regex Path Filter",
          "widget-attributes": {"placeholder": "Regular expression for files to read"}
        },
        {
          "widget-type": "textbox",
          "name": "pathField",
          "label": "Path Field",
          "widget-attributes": {"placeholder": "Output field to contain the path of the object that was read from"}
        },
        {
          "widget-type": "radio-group",
          "name": "filenameOnly",
          "label": "Path Filename Only",
          "widget-attributes": {
            "layout": "inline",
            "default": "false",
            "options": [
              {
                "id": "true",
                "label": "True"
              },
              {
                "id": "false",
                "label": "False"
              }
            ]
          }
        },
        {
          "widget-type": "radio-group",
          "name": "recursive",
          "label": "Read Files Recursively",
          "widget-attributes": {
            "layout": "inline",
            "default": "false",
            "options": [
              {
                "id": "true",
                "label": "True"
              },
              {
                "id": "false",
                "label": "False"
              }
            ]
          }
        },
        {
