properties.widgets.Bigtable-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "label": "schema",
    "widget-attributes": {
      "schema-types": [
        "boolean",
        "int",
        "long",
        "float",
        "double",
        "bytes",
        "string"
      ],
      "schema-default-type": "string"
    }
  }],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this source for lineage"}
        },
        {
          "widget-type": "textbox",
          "name": "project",
          "label": "Project ID",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "instance",
          "label": "Instance ID",
          "widget-attributes": {"placeholder": "Bigtable instance id"}
        },
        {
          "widget-type": "textbox",
          "name": "table",
          "label": "Table",
          "widget-attributes": {"placeholder": "Table to read from"}
        },
        {
          "widget-type": "textbox",
          "name": "keyAlias",
          "label": "Key Alias",
          "widget-attributes": {
            "default": "__key__",
            "placeholder": "Key alias"
          }
        },
        {
          "widget-type": "keyvalue",
          "name": "columnMappings",
          "label": "Column Mappings",
          "widget-attributes": {
            "kv-delimiter": "=",
            "delimiter": ",",
            "placeholder": "Mappings from Bigtable column name to record field. Column names must be formatted as <family>:<qualifier>."
          }
        },
        {
          "widget-type": "keyvalue",
          "name": "bigtableOptions",
          "label": "Bigtable Options",
          "widget-attributes": {
            "kv-delimiter": "=",
            "delimiter": ",",
            "placeholder": "Additional connection properties for Bigtable."
          }
        },
        {
          "widget-type": "textbox",
          "name": "scanRowStart",
          "label": "Scan Start Row",
          "widget-attributes": {"placeholder": "Scan start row"}
        },
        {
          "widget-type": "textbox",
          "name": "scanRowStop",
          "label": "Scan Stop Row",
          "widget-attributes": {"placeholder": "Scan stop row"}
        },
        {
          "widget-type": "textbox",
          "name": "scanTimeRangeStart",
          "label": "Scan Time Range Start",
          "widget-attributes": {"placeholder": "Starting timestamp used to filter columns"}
        },
        {
          "widget-type": "textbox",
          "name": "scanTimeRangeStop",
          "label": "Scan Time Range Stop",
          "widget-attributes": {"placeholder": "Ending timestamp used to filter columns"}
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "name": "serviceAccountType",
          "widget-type": "radio-group",
          "label": "Service Account Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "filePath",
            "options": [
              {
                "id": "filePath",
                "label": "File Path"
              },
              {
                "id": "JSON",
                "label": "JSON"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "serviceFilePath",
          "label": "Service Account File Path",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "serviceAccountJSON",
          "label": "Service Account JSON"
        }
      ]
    },
    {
      "label": "Error Handling",
      "properties": [{
        "name": "on-error",
        "widget-type": "radio-group",
        "label": "On Record Error",
        "widget-attributes": {
          "layout": "block",
          "default": "skip-error",
          "options": [
            {
              "id": "skip-error",
              "label": "Skip error"
            },
            {
              "id": "fail-pipeline",
              "label": "Fail pipeline"
            }
          ]
        }
      }]
    }
  ],
  "display-name": "Bigtable",
