properties.widgets.Spanner-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "widget-attributes": {
      "schema-types": [
        "boolean",
        "long",
        "double",
        "string",
        "bytes"
      ],
      "schema-default-type": "string"
    }
  }],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this source for lineage"}
        },
        {
          "widget-type": "connection-browser",
          "widget-category": "plugin",
          "widget-attributes": {
            "label": "Browse",
            "connectionType": "SPANNER"
          }
        },
        {
          "widget-type": "textbox",
          "name": "project",
          "label": "Project ID",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "instance",
          "label": "Instance ID",
          "widget-attributes": {"placeholder": "Instance ID"}
        },
        {
          "widget-type": "textbox",
          "name": "database",
          "label": "Database Name",
          "widget-attributes": {"placeholder": "Database name"}
        },
        {
          "widget-type": "textbox",
          "name": "table",
          "label": "Table Name",
          "widget-attributes": {"placeholder": "Table name"},
          "plugin-function": {
            "widget": "outputSchema",
            "output-property": "schema",
            "omit-properties": [{"name": "schema"}],
            "label": "Get Schema"
          }
        },
        {
          "widget-type": "textarea",
          "name": "importQuery",
          "label": "Import Query",
          "widget-attributes": {"rows": "4"}
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "name": "serviceAccountType",
          "widget-type": "radio-group",
          "label": "Service Account Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "filePath",
            "options": [
              {
                "id": "filePath",
                "label": "File Path"
              },
              {
                "id": "JSON",
                "label": "JSON"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "serviceFilePath",
          "label": "Service Account File Path",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "serviceAccountJSON",
          "label": "Service Account JSON"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "maxPartitions",
          "label": "Max Partitions",
          "widget-attributes": {"placeholder": "Maximum number of partitions."}
        },
        {
          "widget-type": "textbox",
          "name": "partitionSizeMB",
          "label": "Partition Size (Megabytes)",
          "widget-attributes": {"placeholder": "Partition size in Megabytes."}
        }
      ]
    }
  ],
  "display-name": "Spanner",
