properties.widgets.GCSCopy-action:
{
  "outputs": [],
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "project",
          "label": "Project ID",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "name": "sourcePath",
          "widget-type": "textbox",
          "label": "Source Path",
          "widget-attributes": {"value-placeholder": "gs://bucket/path/to/source"}
        },
        {
          "name": "destPath",
          "widget-type": "textbox",
          "label": "Destination Path",
          "widget-attributes": {"value-placeholder": "gs://bucket/path/to/destination"}
        },
        {
          "widget-type": "radio-group",
          "name": "recursive",
          "label": "Copy All Subdirectories",
          "widget-attributes": {
            "layout": "inline",
            "default": "false",
            "options": [
              {
                "id": "true",
                "label": "True"
              },
              {
                "id": "false",
                "label": "False"
              }
            ]
          }
        },
        {
          "widget-type": "radio-group",
          "name": "overwrite",
          "label": "Overwrite Existing Files",
          "widget-attributes": {
            "layout": "inline",
            "default": "false",
            "options": [
              {
                "id": "true",
                "label": "True"
              },
              {
                "id": "false",
                "label": "False"
              }
            ]
          }
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "name": "serviceAccountType",
          "widget-type": "radio-group",
          "label": "Service Account Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "filePath",
            "options": [
              {
                "id": "filePath",
                "label": "File Path"
              },
              {
                "id": "JSON",
                "label": "JSON"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "serviceFilePath",
          "label": "Service Account File Path",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "serviceAccountJSON",
          "label": "Service Account JSON"
        }
      ]
    }
  ],
