properties.widgets.BigQueryExecute-action:
{
  "outputs": [],
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "project",
          "label": "Project ID",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textarea",
          "name": "sql",
          "label": "SQL",
          "widget-attributes": {"placeholder": "SELECT * from [dataset].[table]"}
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "name": "dialect",
          "widget-type": "radio-group",
          "label": "Dialect",
          "widget-attributes": {
            "layout": "inline",
            "default": "standard",
            "options": [
              {
                "id": "legacy",
                "label": "Legacy"
              },
              {
                "id": "standard",
                "label": "Standard"
              }
            ]
          }
        },
        {
          "name": "mode",
          "widget-type": "radio-group",
          "label": "Mode",
          "widget-attributes": {
            "layout": "inline",
            "default": "batch",
            "options": [
              {
                "id": "batch",
                "label": "Batch"
              },
              {
                "id": "interactive",
                "label": "Interactive"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "dataset",
          "label": "Dataset Name",
          "widget-attributes": {"placeholder": "Name of dataset to store the results."}
        },
        {
          "widget-type": "textbox",
          "name": "table",
          "label": "Table Name",
          "widget-attributes": {"placeholder": "Name of table to store the results."}
        },
        {
          "name": "useCache",
          "widget-type": "toggle",
          "label": "Use Cache",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "False",
              "value": "false"
            },
            "on": {
              "label": "True",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "textbox",
          "name": "location",
          "label": "Job Location",
          "widget-attributes": {
            "default": "US",
            "placeholder": "Location of the job."
          }
        },
        {
          "name": "rowAsArguments",
          "widget-type": "toggle",
          "label": "Row As Arguments",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "False",
              "value": "false"
            },
            "on": {
              "label": "True",
              "value": "true"
            }
          }
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "name": "serviceAccountType",
          "widget-type": "radio-group",
          "label": "Service Account Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "filePath",
            "options": [
              {
                "id": "filePath",
                "label": "File Path"
              },
              {
                "id": "JSON",
                "label": "JSON"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "serviceFilePath",
          "label": "Service Account File Path",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "serviceAccountJSON",
          "label": "Service Account JSON"
        }
      ]
    }
  ],
