properties.widgets.GCSDoneFileMarker-postaction:
{
  "outputs": [],
  "metadata": {"spec-version": "1.0"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "project",
          "label": "Project ID",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "select",
          "name": "runCondition",
          "label": "Run Condition",
          "widget-attributes": {
            "default": "completion",
            "values": [
              "completion",
              "success",
              "failure"
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "path",
          "label": "Path",
          "widget-attributes": {"placeholder": "gs://<bucket-name>/directory/marker-file"}
        }
      ]
    },
    {
