        },
        {
          "widget-type": "select",
          "name": "encoding",
          "label": "Audio Encoding",
          "widget-attributes": {
            "default": "LINEAR16",
            "values": [
              "LINEAR16",
              "AMR",
              "AMR_WB",
              "FLAC",
              "MULAW",
              "OGG_OPUS",
              "SPEEX_WITH_HEADER_BYTE"
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "samplerate",
          "label": "Sampling Rate",
          "widget-attributes": {"placeholder": "Specify the sampling rate in hertz"}
