properties.widgets.HTTP-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "label": "schema",
    "widget-attributes": {
      "schema-types": [
        "boolean",
        "int",
        "long",
        "float",
        "double",
        "bytes",
        "string",
        "array",
        "record",
        "map",
        "union"
      ],
      "schema-default-type": "string",
      "property-watch": "format"
    }
  }],
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "General",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name"
        },
        {
          "widget-type": "textbox",
          "name": "url",
          "label": "URL"
        },
        {
          "widget-type": "select",
          "name": "httpMethod",
          "label": "HTTP Method",
          "widget-attributes": {
            "default": "GET",
            "values": [
              "GET",
              "POST",
              "PUT",
              "DELETE",
              "HEAD"
            ]
          }
        },
        {
          "widget-type": "keyvalue",
          "name": "headers",
          "label": "Headers",
          "widget-attributes": {"showDelimiter": "false"}
        },
        {
          "widget-type": "textarea",
          "name": "requestBody",
          "label": "Request Body",
          "widget-attributes": {"rows": "5"}
        }
      ]
    },
    {
      "label": "Format",
      "properties": [
        {
          "widget-type": "select",
          "name": "format",
          "label": "Format",
          "widget-attributes": {
            "default": "json",
            "values": [
              "json",
              "xml",
              "tsv",
              "csv",
              "text",
              "blob"
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "resultPath",
          "label": "JSON/XML Result Path"
        },
        {
          "widget-type": "keyvalue",
          "name": "fieldsMapping",
          "label": "JSON/XML Fields Mapping",
          "widget-attributes": {"showDelimiter": "false"}
        },
        {
          "widget-type": "radio-group",
          "name": "csvSkipFirstRow",
          "label": "CSV Skip First Row",
          "widget-attributes": {
            "layout": "inline",
            "default": "false",
            "options": [
              {
                "id": "true",
                "label": "true"
              },
              {
                "id": "false",
                "label": "false"
              }
            ]
          }
        }
      ]
    },
    {
      "label": "OAuth2",
      "properties": [
        {
          "widget-type": "toggle",
          "name": "oauth2Enabled",
          "label": "OAuth2 Enabled",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "False",
              "value": "false"
            },
            "on": {
              "label": "True",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "textbox",
          "name": "authUrl",
          "label": "Auth URL"
        },
        {
          "widget-type": "textbox",
          "name": "tokenUrl",
          "label": "Token URL"
        },
        {
          "widget-type": "textbox",
          "name": "clientId",
          "label": "Client ID"
        },
        {
          "widget-type": "password",
          "name": "clientSecret",
          "label": "Client Secret"
        },
        {
          "widget-type": "textbox",
          "name": "scopes",
          "label": "Scopes"
        },
        {
          "widget-type": "textbox",
          "name": "refreshToken",
          "label": "Refresh Token"
        }
      ]
    },
    {
      "label": "Basic Authentication",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "username",
          "label": "Username"
        },
        {
          "widget-type": "password",
          "name": "password",
          "label": "Password"
        }
      ]
    },
    {
      "label": "HTTP Proxy",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "proxyUrl",
          "label": "Proxy URL"
        },
        {
          "widget-type": "textbox",
          "name": "proxyUsername",
          "label": "Username"
        },
        {
          "widget-type": "password",
          "name": "proxyPassword",
          "label": "Password"
        }
      ]
    },
    {
      "label": "Error Handling",
      "properties": [
        {
          "widget-type": "keyvalue-dropdown",
          "name": "httpErrorsHandling",
          "label": "HTTP Errors Handling",
          "widget-attributes": {
            "default": "2..:Success,.*:Fail",
            "dropdownOptions": [
              "Success",
              "Fail",
              "Skip",
              "Send to error",
              "Retry and fail",
              "Retry and skip",
              "Retry and send to error"
            ],
            "key-placeholder": "HTTP Status Code Regex",
            "showDelimiter": "false"
          }
        },
        {
          "widget-type": "radio-group",
          "name": "errorHandling",
          "label": "Non-HTTP Error Handling",
          "widget-attributes": {
            "layout": "inline",
            "default": "stopOnError",
            "options": [
              {
                "id": "stopOnError",
                "label": "Stop on error"
              },
              {
                "id": "sendToError",
                "label": "Send to error"
              },
              {
                "id": "skipOnError",
                "label": "Skip on error"
              }
            ]
          }
        },
        {
          "widget-type": "radio-group",
          "name": "retryPolicy",
          "label": "Retry Policy",
          "widget-attributes": {
            "layout": "inline",
            "default": "exponential",
            "options": [
              {
                "id": "exponential",
                "label": "Exponential"
              },
              {
                "id": "linear",
                "label": "Linear"
              }
            ]
          }
        },
        {
          "widget-type": "number",
          "name": "linearRetryInterval",
          "label": "Linear Retry Interval",
          "widget-attributes": {
            "default": "30",
            "min": "0"
          }
        },
        {
          "widget-type": "number",
          "name": "maxRetryDuration",
          "label": "Max Retry Duration",
          "widget-attributes": {
            "default": "600",
            "min": "0"
          }
        },
        {
          "widget-type": "number",
          "name": "connectTimeout",
          "label": "Connect Timeout",
          "widget-attributes": {
            "default": "120",
            "min": "0"
          }
        },
        {
          "widget-type": "number",
          "name": "readTimeout",
          "label": "Read Timeout",
          "widget-attributes": {
            "default": "120",
            "min": "0"
          }
        }
      ]
    },
    {
      "label": "Pagination",
      "properties": [
        {
          "widget-type": "select",
          "name": "paginationType",
          "label": "Pagination Type",
          "widget-attributes": {
            "default": "None",
            "values": [
              "None",
              "Link in response header",
              "Link in response body",
              "Token in response body",
              "Increment an index",
              "Custom"
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "startIndex",
          "label": "Start Index",
          "widget-attributes": {"placeholder": "For pagination type \"Increment an index\""}
        },
        {
          "widget-type": "textbox",
          "name": "maxIndex",
          "label": "Max Index",
          "widget-attributes": {"placeholder": "For pagination type \"Increment an index\""}
        },
        {
          "widget-type": "textbox",
          "name": "indexIncrement",
          "label": "Index Increment",
          "widget-attributes": {"placeholder": "For pagination type \"Increment an index\""}
        },
        {
          "widget-type": "textbox",
          "name": "nextPageFieldPath",
          "label": "Next Page JSON/XML Field Path",
          "widget-attributes": {"placeholder": "For pagination type \"Link in response body\""}
        },
        {
          "widget-type": "textbox",
          "name": "nextPageTokenPath",
          "label": "Next Page Token Path",
          "widget-attributes": {"placeholder": "For pagination type \"Token in response body\""}
        },
        {
          "widget-type": "textbox",
          "name": "nextPageUrlParameter",
          "label": "Next Page Url Parameter",
          "widget-attributes": {"placeholder": "For pagination type \"Token in response body\""}
        },
        {
          "widget-type": "python-editor",
          "name": "customPaginationCode",
          "label": "Custom Pagination Python Code",
          "widget-attributes": {"placeholder": "def get_next_page_url(url, page, headers):\n    \"\"\"\n    Based on previous page data generates next page url, when \"Custom pagination\" is enabled.\n    Returns 'None' if no more pages to load \n\n    Args:\n        url (string): previous page url\n        page (string): a body of previous page\n        headers (dict): a dictionary of headers from previous page\n\n    \"\"\"\n    return \"https://next-page-url.com\""}
        },
        {
          "widget-type": "number",
          "name": "waitTimeBetweenPages",
          "label": "Wait Time Between Pages (milliseconds)",
          "widget-attributes": {
            "default": "0",
            "min": "0"
          }
        }
      ]
    },
    {
      "label": "SSL/TLS",
      "properties": [
        {
          "widget-type": "toggle",
          "name": "verifyHttps",
          "label": "Verify HTTPS Trust Certificates",
          "widget-attributes": {
            "default": "true",
            "off": {
              "label": "False",
              "value": "false"
            },
            "on": {
              "label": "True",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "textbox",
          "name": "keystoreFile",
          "label": "Keystore File"
        },
        {
          "widget-type": "select",
          "name": "keystoreType",
          "label": "Keystore Type",
          "widget-attributes": {
            "default": "Java KeyStore (JKS)",
            "values": [
              "Java KeyStore (JKS)",
              "Java Cryptography Extension KeyStore (JCEKS)",
              "PKCS #12"
            ]
          }
        },
        {
          "widget-type": "password",
          "name": "keystorePassword",
          "label": "Keystore Password"
        },
        {
          "widget-type": "textbox",
          "name": "keystoreKeyAlgorithm",
          "label": "Keystore Key Algorithm",
          "widget-attributes": {"default": "SunX509"}
        },
        {
          "widget-type": "textbox",
          "name": "trustStoreFile",
          "label": "TrustStore File"
        },
        {
          "widget-type": "select",
          "name": "trustStoreType",
          "label": "TrustStore Type",
          "widget-attributes": {
            "default": "Java KeyStore (JKS)",
            "values": [
              "Java KeyStore (JKS)",
              "Java Cryptography Extension KeyStore (JCEKS)",
              "PKCS #12"
            ]
          }
        },
        {
          "widget-type": "password",
          "name": "trustStorePassword",
          "label": "TrustStore Password"
        },
        {
          "widget-type": "textbox",
          "name": "trustStoreKeyAlgorithm",
          "label": "TrustStore Key Algorithm",
          "widget-attributes": {"default": "SunX509"}
        },
        {
          "widget-type": "csv",
          "name": "transportProtocols",
          "label": "Transport Protocols",
          "widget-attributes": {"default": "TLSv1.2"}
        },
        {
          "widget-type": "textbox",
          "name": "cipherSuites",
          "label": "Cipher Suites"
        }
      ]
    }
  ],
