properties.widgets.MongoDB-batchsink:
{
  "outputs": [],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this sink for lineage"}
        },
        {
          "widget-type": "textbox",
          "name": "host",
          "label": "Host"
        },
        {
          "widget-type": "number",
          "name": "port",
          "label": "Port",
          "widget-attributes": {"default": "27017"}
        },
        {
          "widget-type": "textbox",
          "name": "database",
          "label": "Database"
        },
        {
          "widget-type": "textbox",
          "name": "collection",
          "label": "Collection"
        },
        {
          "widget-type": "textbox",
          "name": "idField",
          "label": "ID Field"
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "user",
          "label": "Username"
        },
        {
          "widget-type": "password",
          "name": "password",
          "label": "Password"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [{
        "widget-type": "keyvalue",
        "name": "connectionArguments",
        "label": "Connection Arguments",
        "widget-attributes": {
          "kv-delimiter": "=",
          "key-placeholder": "Key",
          "value-placeholder": "Value",
          "delimiter": ";",
          "showDelimiter": "false"
        }
      }]
    }
  ],
  "display-name": "MongoDB"
}

properties.widgets.MongoDB-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "label": "schema",
    "widget-attributes": {
      "schema-types": [
        "boolean",
        "int",
        "long",
        "float",
        "double",
        "bytes",
        "string"
      ],
      "schema-default-type": "string",
      "property-watch": "format"
    }
  }],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this source for lineage"}
        },
        {
          "widget-type": "textbox",
          "name": "host",
          "label": "Host"
        },
        {
          "widget-type": "number",
          "name": "port",
          "label": "Port",
          "widget-attributes": {"default": "27017"}
        },
        {
          "widget-type": "textbox",
          "name": "database",
          "label": "Database"
        },
        {
          "widget-type": "textbox",
          "name": "collection",
          "label": "Collection"
        },
        {
          "widget-type": "json-editor",
          "name": "inputQuery",
          "label": "Input Query",
          "widget-attributes": {"rows": "4"}
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "user",
          "label": "Username"
        },
        {
          "widget-type": "password",
          "name": "password",
          "label": "Password"
        },
        {
          "widget-type": "textbox",
          "name": "authConnectionString",
          "label": "Authentication Connection String",
          "widget-attributes": {"width": "large"}
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "name": "on-error",
          "widget-type": "radio-group",
          "label": "On Record Error",
          "widget-attributes": {
            "layout": "block",
            "default": "fail-pipeline",
            "options": [
              {
                "id": "send-to-error",
                "label": "Send to error"
              },
              {
                "id": "skip-error",
                "label": "Skip error"
              },
              {
                "id": "fail-pipeline",
                "label": "Fail pipeline"
              }
            ]
          }
        },
        {
          "widget-type": "keyvalue",
          "name": "connectionArguments",
          "label": "Connection Arguments",
          "widget-attributes": {
            "kv-delimiter": "=",
            "key-placeholder": "Key",
            "value-placeholder": "Value",
            "delimiter": ";",
            "showDelimiter": "false"
          }
        }
      ]
    }
  ],
  "display-name": "MongoDB"
}
