properties.widgets.Ariba-batchsource:
{
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Used to uniquely identify this source for lineage, annotating metadata etc."}
        },
        {
          "widget-type": "textbox",
          "name": "baseURL",
          "label": "API Endpoint",
          "widget-attributes": {"placeholder": "Base path of the Ariba API, for example, https://sandbox.api.sap.com"}
        },
        {
          "widget-type": "radio-group",
          "name": "systemType",
          "label": "System Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "prod",
            "options": [
              {
                "id": "prod",
                "label": "Production"
              },
              {
                "id": "sandbox",
                "label": "Sandbox"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "realm",
          "label": "Realm",
          "widget-attributes": {"placeholder": "Realm name from which the data is to be extracted, for example, S4VALL."}
        },
        {
          "widget-type": "textbox",
          "name": "viewTemplateName",
          "label": "View Template Name",
          "widget-attributes": {"placeholder": "Name of the view template from which data is to be extracted, for example, RequestFactSystemView."}
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "clientId",
          "label": "Ariba Client ID",
          "widget-attributes": {"placeholder": "Ariba Client ID"}
        },
        {
          "widget-type": "password",
          "name": "clientSecret",
          "label": "Ariba Client Secret",
          "widget-attributes": {"placeholder": "Ariba Client Secret"}
        },
        {
          "widget-type": "password",
          "name": "apiKey",
          "label": "Ariba API Key",
          "widget-attributes": {"placeholder": "Use any Application Key of an Application(s) you created in developer portal."}
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "fromDate",
          "label": "From Date",
          "widget-attributes": {"placeholder": "Start date of the extraction, for example, 2021-12-01T00:00:00Z."}
        },
        {
          "widget-type": "textbox",
          "name": "toDate",
          "label": "To Date",
          "widget-attributes": {"placeholder": "End date of the extraction, for example, 2022-03-29T00:00:00Z."}
        }
      ]
    }
  ],
  "display-name": "SAP Ariba",
