properties.widgets.FTP-batchsource:
{
  "outputs": [{
    "schema": {
      "name": "etlSchemaBody",
      "type": "record",
      "fields": [
        {
          "name": "offset",
          "type": "long"
        },
        {
          "name": "body",
          "type": "string"
        }
      ]
    },
    "widget-type": "non-editable-schema-editor"
  }],
  "jump-config": {"datasets": [{"ref-property-name": "referenceName"}]},
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name",
          "widget-attributes": {"placeholder": "Name used to identify this source for lineage"}
        },
        {
          "widget-type": "textbox",
          "name": "path",
          "label": "Path"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "fileRegex",
          "label": "Regex Path Filter"
        },
        {
          "widget-type": "json-editor",
          "name": "fileSystemProperties",
          "label": "File System Properties"
        },
        {
          "widget-type": "radio-group",
          "name": "ignoreNonExistingFolders",
          "label": "Allow Empty Input",
          "widget-attributes": {
            "layout": "inline",
            "default": "false",
            "options": [
              {
                "id": "true",
                "label": "True"
              },
              {
                "id": "false",
                "label": "False"
              }
            ]
          }
        }
      ]
    }
  ]
}
