            "default": "binary",
            "delimiter": ",",
            "options": [
              {
                "id": "binary",
                "label": "Binary"
              },
              {
                "id": "documents",
                "label": "Google Documents"
              },
              {
                "id": "spreadsheets",
                "label": "Google Spreadsheets"
              },
              {
                "id": "drawings",
                "label": "Google Drawings"
              },
              {
                "id": "presentations",
                "label": "Google Presentations"
              },
              {
                "id": "appsScripts",
                "label": "Google Apps Scripts"
              }
            ]
          }
        }
      ]
    },
    {
      "label": "Authentication",
      "properties": [
        {
          "widget-type": "radio-group",
          "name": "authType",
          "label": "Authentication Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "oAuth2",
            "options": [
              {
                "id": "oAuth2",
                "label": "OAuth2"
              },
              {
                "id": "serviceAccount",
                "label": "Service account"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "clientId",
          "label": "Client ID"
        },
        {
          "widget-type": "password",
          "name": "clientSecret",
          "label": "Client Secret"
        },
        {
          "widget-type": "password",
          "name": "refreshToken",
          "label": "Refresh Token"
        },
        {
          "name": "serviceAccountType",
          "widget-type": "radio-group",
          "label": "Service Account Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "filePath",
            "options": [
              {
                "id": "filePath",
                "label": "File Path"
              },
              {
                "id": "JSON",
                "label": "JSON"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "accountFilePath",
          "label": "Service Account File Path",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "serviceAccountJSON",
          "label": "Service Account JSON"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "number",
          "name": "maxPartitionSize",
          "label": "Maximum Partition Size",
          "widget-attributes": {
            "default": "0",
            "min": "0"
          }
        },
        {
          "widget-type": "radio-group",
          "name": "bodyFormat",
          "label": "Body Output Format",
          "widget-attributes": {
            "layout": "inline",
            "default": "bytes",
            "options": [
              {
                "id": "bytes",
                "label": "Bytes"
              },
              {
                "id": "string",
                "label": "String"
              }
            ]
          }
        }
      ]
    },
    {
      "label": "Exporting",
      "properties": [
        {
          "widget-type": "select",
          "name": "docsExportingFormat",
          "label": "Google Documents Export Format",
          "widget-attributes": {
            "default": "text/plain",
            "values": [
              "text/html",
              "application/zip",
              "text/plain",
              "application/rtf",
              "application/vnd.oasis.opendocument.text",
              "application/pdf",
              "application/vnd.openxmlformats-officedocument.wordprocessingml.document",
              "application/epub+zip"
            ]
          }
        },
        {
          "widget-type": "select",
          "name": "sheetsExportingFormat",
          "label": "Google Spreadsheets Export Format",
          "widget-attributes": {
            "default": "text/csv",
            "values": [
              "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet",
              "application/x-vnd.oasis.opendocument.spreadsheet",
              "application/pdf",
              "text/csv",
              "text/tab-separated-values",
              "application/zip"
            ]
          }
        },
        {
          "widget-type": "select",
          "name": "drawingsExportingFormat",
          "label": "Google Drawings Export Format",
          "widget-attributes": {
            "default": "image/svg+xml",
