  "filters": [
    {
      "condition": {
        "property": "modificationDateRange",
        "value": "custom",
        "operator": "equal to"
      },
      "name": "Select modification date range",
      "show": [
        {
          "name": "startDate",
          "type": "property"
        },
        {
          "name": "endDate",
          "type": "property"
        }
      ]
    },
    {
      "condition": {
        "property": "authType",
        "value": "oAuth2",
        "operator": "equal to"
      },
      "name": "Authenticate with OAuth2",
      "show": [
        {
          "name": "clientId",
          "type": "property"
        },
        {
          "name": "clientSecret",
          "type": "property"
        },
        {
          "name": "refreshToken",
          "type": "property"
        }
      ]
    },
    {
      "condition": {
        "property": "authType",
        "value": "serviceAccount",
        "operator": "equal to"
      },
      "name": "Authenticate with service account",
      "show": [{
        "name": "serviceAccountType",
        "type": "property"
      }]
    },
    {
      "condition": {"expression": "authType == 'serviceAccount' && serviceAccountType == 'filePath'"},
      "name": "ServiceAuthenticationTypeFilePath",
      "show": [{
        "name": "accountFilePath",
        "type": "property"
      }]
    },
    {
      "condition": {"expression": "authType == 'serviceAccount' && serviceAccountType == 'JSON'"},
      "name": "ServiceAuthenticationTypeJSON",
      "show": [{
        "name": "serviceAccountJSON",
        "type": "property"
      }]
    }
  ]
}

properties.widgets.GoogleSheets-batchsource:
{
  "outputs": [],
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name"
        },
        {
          "widget-type": "textbox",
          "name": "directoryIdentifier",
          "label": "Directory Identifier"
        }
      ]
    },
    {
      "label": "Filtering",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "filter",
          "label": "Filter"
        },
        {
          "widget-type": "select",
          "name": "modificationDateRange",
          "label": "Modification Date Range",
          "widget-attributes": {
            "default": "lifetime",
            "values": [
              "today",
              "yesterday",
              "this_week_sun_today",
              "this_week_mon_today",
              "last_week_sun_sat",
              "last_week_mon_sun",
              "this_month",
              "last_month",
              "this_quarter",
              "last_3d",
              "last_7d",
              "last_14d",
              "last_28d",
              "last_30d",
              "last_90d",
              "this_year",
              "last_year",
              "lifetime",
              "custom"
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "startDate",
          "label": "Start Date"
        },
        {
          "widget-type": "textbox",
          "name": "endDate",
          "label": "End Date"
        },
        {
          "name": "sheetsToPull",
          "widget-type": "select",
          "label": "Sheets To Pull",
          "widget-attributes": {
            "default": "all",
            "values": [
              "all",
              "numbers",
              "titles"
            ]
          }
        },
        {
          "name": "sheetsIdentifiers",
          "widget-type": "csv",
          "label": "Sheets Identifiers",
          "widget-attributes": {"delimiter": ","}
        }
      ]
    },
    {
      "label": "Authentication",
      "properties": [
        {
          "widget-type": "radio-group",
          "name": "authType",
          "label": "Authentication Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "oAuth2",
            "options": [
              {
                "id": "oAuth2",
                "label": "OAuth2"
              },
              {
                "id": "serviceAccount",
                "label": "Service account"
              }
            ]
          }
        },
        {
          "name": "serviceAccountType",
          "widget-type": "radio-group",
          "label": "Service Account Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "filePath",
            "options": [
              {
                "id": "filePath",
                "label": "File Path"
              },
              {
                "id": "JSON",
                "label": "JSON"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "accountFilePath",
          "label": "Account File Path",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "serviceAccountJSON",
          "label": "Service Account JSON"
        },
        {
          "widget-type": "textbox",
          "name": "clientId",
          "label": "Client ID"
        },
        {
          "widget-type": "password",
          "name": "clientSecret",
          "label": "Client Secret"
        },
        {
          "widget-type": "password",
          "name": "refreshToken",
          "label": "Refresh Token"
        }
      ]
    },
    {
      "label": "Metadata Extraction",
      "properties": [
        {
          "widget-type": "toggle",
          "name": "extractMetadata",
          "label": "Extract Metadata",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "No",
              "value": "false"
            },
            "on": {
              "label": "Yes",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "textbox",
          "name": "metadataFieldName",
          "label": "Metadata Field Name",
          "widget-attributes": {"default": "metadata"}
        },
        {
          "widget-type": "number",
          "name": "firstHeaderRow",
          "label": "First Row of Header",
          "widget-attributes": {
            "default": null,
            "min": "1"
          }
        },
        {
          "widget-type": "number",
          "name": "lastHeaderRow",
          "label": "Last Row of Header",
          "widget-attributes": {
            "default": null,
            "min": "1"
          }
        },
        {
          "widget-type": "number",
          "name": "firstFooterRow",
          "label": "First Row of Footer",
          "widget-attributes": {
            "default": null,
            "min": "1"
          }
        },
        {
          "widget-type": "number",
          "name": "lastFooterRow",
          "label": "Last Row of Footer",
          "widget-attributes": {
            "default": null,
            "min": "1"
          }
        },
        {
          "widget-type": "keyvalue",
          "name": "metadataCells",
          "label": "Metadata Cells",
          "widget-attributes": {
            "kv-delimiter": ":",
            "delimiter": ","
          }
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "radio-group",
          "name": "formatting",
          "label": "Text Formatting",
          "widget-attributes": {
            "layout": "inline",
            "default": "valuesOnly",
            "options": [
              {
                "id": "valuesOnly",
                "label": "Values only"
              },
              {
                "id": "formattedValues",
                "label": "Formatted values"
              }
            ]
          }
        },
        {
          "widget-type": "toggle",
          "name": "skipEmptyData",
          "label": "Skip Empty Data",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "No",
              "value": "false"
            },
            "on": {
              "label": "Yes",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "toggle",
          "name": "addNameFields",
          "label": "Add spreadsheet/sheet name fields",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "No",
              "value": "false"
            },
            "on": {
              "label": "Yes",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "textbox",
          "name": "spreadsheetFieldName",
          "label": "Spreadsheet field name",
          "widget-attributes": {"default": "spreadsheetName"}
        },
        {
          "widget-type": "textbox",
          "name": "sheetFieldName",
          "label": "Sheet field name",
          "widget-attributes": {"default": "sheetName"}
        },
        {
          "widget-type": "radio-group",
          "name": "columnNamesSelection",
          "label": "Column Names Selection",
          "widget-attributes": {
            "layout": "inline",
            "default": "firstRowAsColumns",
            "options": [
              {
                "id": "firstRowAsColumns",
                "label": "Treat first row as column names"
              },
              {
                "id": "customRowAsColumns",
                "label": "Custom row as column names"
              },
              {
                "id": "noColumnNames",
                "label": "No column names"
              }
            ]
          }
        },
        {
          "widget-type": "number",
          "name": "customColumnNamesRow",
          "label": "Column Names Row Number",
          "widget-attributes": {
            "default": "1",
            "min": "1"
          }
        },
        {
          "widget-type": "number",
          "name": "lastDataColumn",
          "label": "Number of Columns to Read",
          "widget-attributes": {
            "default": "26",
            "min": "0"
          }
        },
        {
          "widget-type": "number",
          "name": "lastDataRow",
          "label": "Number of Rows to Read",
          "widget-attributes": {
            "default": "1000",
            "min": "0"
          }
        },
        {
          "widget-type": "number",
          "name": "readBufferSize",
          "label": "Read Buffer Size",
          "widget-attributes": {
            "default": "100",
            "min": "1"
          }
        }
      ]
    }
  ],
  "display-name": "Google Sheets Source",
