  "filters": [
    {
      "condition": {
        "property": "modificationDateRange",
        "value": "custom",
        "operator": "equal to"
      },
      "name": "Select modification date range",
      "show": [
        {
          "name": "startDate",
          "type": "property"
        },
        {
          "name": "endDate",
          "type": "property"
        }
      ]
    },
    {
      "condition": {"expression": "sheetsToPull == 'numbers' || sheetsToPull == 'titles'"},
      "name": "Sheet numbers",
      "show": [{
        "name": "sheetsIdentifiers",
        "type": "property"
      }]
    },
    {
      "condition": {
        "property": "authType",
        "value": "oAuth2",
        "operator": "equal to"
      },
      "name": "Authenticate with OAuth2",
      "show": [
        {
          "name": "clientId",
          "type": "property"
        },
        {
          "name": "clientSecret",
          "type": "property"
        },
        {
          "name": "refreshToken",
          "type": "property"
        }
      ]
    },
    {
      "condition": {
        "property": "authType",
        "value": "serviceAccount",
        "operator": "equal to"
      },
      "name": "Authenticate with service account",
      "show": [{
        "name": "serviceAccountType",
        "type": "property"
      }]
    },
    {
      "condition": {"expression": "authType == 'serviceAccount' && serviceAccountType == 'filePath'"},
      "name": "ServiceAuthenticationTypeFilePath",
      "show": [{
        "name": "accountFilePath",
        "type": "property"
      }]
    },
    {
      "condition": {"expression": "authType == 'serviceAccount' && serviceAccountType == 'JSON'"},
      "name": "ServiceAuthenticationTypeJSON",
      "show": [{
        "name": "serviceAccountJSON",
        "type": "property"
      }]
    },
    {
      "condition": {
        "property": "columnNamesSelection",
        "value": "customRowAsColumns",
        "operator": "equal to"
      },
      "name": "Custom header filter",
      "show": [{
        "name": "customColumnNamesRow",
        "type": "property"
      }]
    },
    {
      "condition": {
        "property": "extractMetadata",
        "value": "true",
        "operator": "equal to"
      },
      "name": "Extract metadata",
      "show": [
        {
          "name": "metadataRecordName",
          "type": "property"
        },
        {
          "name": "firstHeaderRow",
          "type": "property"
        },
        {
          "name": "lastHeaderRow",
          "type": "property"
        },
        {
          "name": "firstFooterRow",
          "type": "property"
        },
        {
          "name": "lastFooterRow",
          "type": "property"
        },
        {
          "name": "metadataCells",
          "type": "property"
        }
      ]
    },
    {
      "condition": {
        "property": "addNameFields",
        "value": "true",
        "operator": "equal to"
      },
      "name": "Name fields",
      "show": [
        {
          "name": "spreadsheetFieldName",
          "type": "property"
        },
        {
          "name": "sheetFieldName",
          "type": "property"
        }
      ]
    }
  ]
}

properties.widgets.GoogleSheets-batchsink:
{
  "outputs": [],
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name"
        },
        {
          "widget-type": "textbox",
          "name": "directoryIdentifier",
          "label": "Directory Identifier"
        },
        {
          "widget-type": "textbox",
          "name": "schemaSpreadsheetNameFieldName",
          "label": "Spreadsheet Name Field"
        },
        {
          "widget-type": "textbox",
          "name": "spreadsheetName",
          "label": "Default Spreadsheet Name",
          "widget-attributes": {"default": "Spreadsheet 1"}
        },
        {
          "widget-type": "textbox",
          "name": "schemaSheetNameFieldName",
          "label": "Sheet Name Field"
        },
        {
          "widget-type": "textbox",
          "name": "sheetName",
          "label": "Default Sheet Name",
          "widget-attributes": {"default": "Sheet 1"}
        },
        {
          "widget-type": "toggle",
          "name": "writeSchema",
          "label": "Write Schema As First Row",
          "widget-attributes": {
            "default": "true",
            "off": {
              "label": "No",
              "value": "false"
            },
            "on": {
              "label": "Yes",
              "value": "true"
            }
          }
        }
      ]
    },
    {
      "label": "Authentication",
      "properties": [
        {
          "widget-type": "radio-group",
          "name": "authType",
          "label": "Authentication Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "oAuth2",
            "options": [
              {
                "id": "oAuth2",
                "label": "OAuth2"
              },
              {
                "id": "serviceAccount",
                "label": "Service account"
              }
            ]
          }
        },
        {
          "name": "serviceAccountType",
          "widget-type": "radio-group",
          "label": "Service Account Type",
          "widget-attributes": {
            "layout": "inline",
            "default": "filePath",
            "options": [
              {
                "id": "filePath",
                "label": "File Path"
              },
              {
                "id": "JSON",
                "label": "JSON"
              }
            ]
          }
        },
        {
          "widget-type": "textbox",
          "name": "accountFilePath",
          "label": "Account File Path",
          "widget-attributes": {"default": "auto-detect"}
        },
        {
          "widget-type": "textbox",
          "name": "serviceAccountJSON",
          "label": "Service Account JSON"
        },
        {
          "widget-type": "textbox",
          "name": "clientId",
          "label": "Client ID"
        },
        {
          "widget-type": "password",
          "name": "clientSecret",
          "label": "Client Secret"
        },
        {
          "widget-type": "password",
          "name": "refreshToken",
          "label": "Refresh Token"
        }
      ]
    },
    {
      "label": "Buffering And Paralellization",
      "properties": [
        {
          "widget-type": "number",
          "name": "threadsNumber",
          "label": "Threads Number",
          "widget-attributes": {
            "default": "5",
            "min": "1"
          }
        },
        {
          "widget-type": "number",
          "name": "maxBufferSize",
          "label": "Maximal Buffer Size",
          "widget-attributes": {
            "default": "50",
            "min": "1"
          }
        },
        {
          "widget-type": "number",
          "name": "recordsQueueLength",
          "label": "Records Queue Length",
          "widget-attributes": {
            "default": "500",
            "min": "1"
          }
        },
        {
          "widget-type": "number",
          "name": "maxFlushInterval",
          "label": "Maximum Flush Interval",
          "widget-attributes": {
            "default": "10",
            "min": "1"
          }
        },
        {
          "widget-type": "number",
          "name": "flushExecutionTimeout",
          "label": "Flush Execution Timeout",
          "widget-attributes": {
            "default": "500",
            "min": "1"
          }
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "number",
          "name": "minPageExtensionSize",
          "label": "Minimal Page Extension Size",
          "widget-attributes": {
            "default": "1000",
            "min": "1"
          }
        },
        {
          "widget-type": "toggle",
          "name": "mergeDataCells",
          "label": "Merge Data Cells",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "No",
              "value": "false"
            },
            "on": {
              "label": "Yes",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "toggle",
          "name": "skipNameFields",
          "label": "Skip spreadsheet/sheet name fields",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "No",
              "value": "false"
            },
            "on": {
              "label": "Yes",
              "value": "true"
            }
          }
        }
      ]
    }
  ],
