properties.widgets.Snowflake-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "widget-attributes": {
      "schema-types": [
        "boolean",
        "bytes",
        "date",
        "double",
        "decimal",
        "string",
        "time",
        "timestamp",
        "array",
        "record"
      ],
      "schema-default-type": "string"
    }
  }],
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name"
        },
        {
          "widget-type": "textbox",
          "name": "accountName",
          "label": "Account Name"
        },
        {
          "widget-type": "textbox",
          "name": "database",
          "label": "Database"
        },
        {
          "widget-type": "textbox",
          "name": "schemaName",
          "label": "Schema"
        },
        {
          "widget-type": "textbox",
          "name": "warehouse",
          "label": "Warehouse"
        },
        {
          "widget-type": "textbox",
          "name": "role",
          "label": "Role"
        },
        {
          "widget-type": "textarea",
          "name": "importQuery",
          "label": "Import Query",
          "plugin-function": {
            "widget": "outputSchema",
            "output-property": "schema",
            "omit-properties": [{"name": "schema"}],
            "label": "Get Schema"
          }
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "username",
          "label": "Username"
        },
        {
          "widget-type": "password",
          "name": "password",
          "label": "Password"
        }
      ]
    },
    {
      "label": "Key Pair Authentication",
      "properties": [
        {
          "widget-type": "toggle",
          "name": "keyPairEnabled",
          "label": "Key Pair Authentication Enabled",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "False",
              "value": "false"
            },
            "on": {
              "label": "True",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "textarea",
          "name": "privateKey",
          "label": "Private Key"
        },
        {
          "widget-type": "password",
          "name": "passphrase",
          "label": "Key File Passphrase"
        }
      ]
    },
    {
      "label": "OAuth2",
      "properties": [
        {
          "widget-type": "toggle",
          "name": "oauth2Enabled",
          "label": "OAuth2 Enabled",
          "widget-attributes": {
            "default": "false",
            "off": {
              "label": "False",
              "value": "false"
            },
            "on": {
              "label": "True",
              "value": "true"
            }
          }
        },
        {
          "widget-type": "textbox",
          "name": "clientId",
          "label": "Client ID"
        },
        {
          "widget-type": "password",
          "name": "clientSecret",
          "label": "Client Secret"
        },
        {
          "widget-type": "textbox",
          "name": "refreshToken",
          "label": "Refresh Token"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "number",
          "name": "maxSplitSize",
          "label": "Maximum Split Size",
          "widget-attributes": {
            "default": "0",
            "min": "0"
          }
        },
        {
          "widget-type": "keyvalue",
          "name": "connectionArguments",
          "label": "Connection Arguments",
          "widget-attributes": {
            "key-placeholder": "Connection Argument",
            "value-placeholder": "Connection Argument Value",
            "showDelimiter": "false"
          }
        }
      ]
    }
  ],
