      "name": "SourceFilterPath",
      "show": [{
        "name": "sourcePath",
        "type": "property"
      }]
    },
    {
      "condition": {
        "property": "sourceType",
        "value": "From Query",
        "operator": "equal to"
      },
      "name": "SourceFilterQuery",
      "show": [{
        "name": "sourceQuery",
        "type": "property"
      }]
    },
    {
      "condition": {
        "property": "keyPairEnabled",
        "value": "true",
        "operator": "equal to"
      },
      "name": "KeyPairAuthenticationEnabledFilter",
      "show": [
        {
          "name": "privateKey",
          "type": "property"
        },
        {
          "name": "passphrase",
          "type": "property"
        }
      ]
    },
    {
      "condition": {
        "property": "oauth2Enabled",
        "value": "true",
        "operator": "equal to"
      },
      "name": "OAuth2EnabledFilter",
      "show": [
        {
          "name": "clientId",
          "type": "property"
        },
        {
          "name": "clientSecret",
          "type": "property"
        },
        {
          "name": "refreshToken",
          "type": "property"
        }
      ]
    },
    {
      "condition": {
        "property": "useCloudProviderParameters",
        "value": "true",
        "operator": "equal to"
      },
      "name": "CloudProviderParametersFilter",
      "show": [{
        "name": "cloudProvider",
        "type": "property"
      }]
    },
    {
      "condition": {"expression": "cloudProvider != 'NONE'"},
      "name": "AwsOrGCPOrAzureFilter",
      "show": [
        {
          "name": "storageIntegration",
          "type": "property"
        },
        {
          "name": "filesEncrypted",
          "type": "property"
        }
      ]
    },
    {
      "condition": {
        "property": "cloudProvider",
        "value": "AWS",
        "operator": "equal to"
      },
      "name": "AwsCloudProviderFilter",
      "show": [
        {
          "name": "awsKeyId",
          "type": "property"
        },
        {
          "name": "awsSecretKey",
          "type": "property"
        },
        {
          "name": "awsToken",
          "type": "property"
        }
      ]
    },
    {
      "condition": {
        "property": "cloudProvider",
        "value": "Microsoft Azure",
        "operator": "equal to"
      },
      "name": "AzureCloudProviderFilter",
      "show": [{
        "name": "azureSasToken",
        "type": "property"
      }]
    },
    {
      "condition": {
        "property": "filesEncrypted",
        "value": "true",
        "operator": "equal to"
      },
      "name": "FilesEncryptedFilter",
      "show": [{
        "name": "encryptionType",
        "type": "property"
      }]
    },
    {
      "condition": {"expression": "encryptionType == 'AWS_CSE' || encryptionType == 'AZURE_CSE'"},
      "name": "CSEEncryptionTypeFilter",
      "show": [{
        "name": "masterKey",
        "type": "property"
      }]
    },
    {
      "condition": {"expression": "encryptionType == 'AWS_SSE_KMS' || encryptionType == 'GCS_SSE_KMS'"},
      "name": "KMSEncryptionTypeFilter",
      "show": [{
        "name": "kmsKeyId",
        "type": "property"
      }]
    },
    {
      "condition": {
        "property": "fileFormatFilteringPolicy",
        "value": "By Existing Format Specification",
        "operator": "equal to"
      },
      "name": "NameFileFormatFilter",
      "show": [{
        "name": "formatName",
        "type": "property"
      }]
    },
    {
      "condition": {
        "property": "fileFormatFilteringPolicy",
        "value": "By File Type",
        "operator": "equal to"
      },
      "name": "TypeFileFormatFilter",
      "show": [{
        "name": "formatType",
        "type": "property"
      }]
    },
    {
      "condition": {"expression": "formatType != 'ORC' && formatType != 'NONE'"},
      "name": "FileTypeFilter",
      "show": [{
        "name": "formatTypeOptions",
        "type": "property"
      }]
    }
  ]
}

