properties.widgets.Zendesk-batchsource:
{
  "outputs": [{
    "name": "schema",
    "widget-type": "schema",
    "widget-attributes": {"schema-default-type": "string"}
  }],
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "widget-type": "textbox",
          "name": "referenceName",
          "label": "Reference Name"
        },
        {
          "widget-type": "textbox",
          "name": "adminEmail",
          "label": "Admin Email"
        },
        {
          "widget-type": "password",
          "name": "apiToken",
          "label": "API Token"
        },
        {
          "widget-type": "csv",
          "name": "subdomains",
          "label": "Subdomains",
          "widget-attributes": {
            "value-placeholder": "List of Subdomains to read object from.",
            "delimiter": ","
          }
        },
        {
          "name": "objectsToPull",
          "widget-type": "select",
          "label": "Object to Pull",
          "widget-attributes": {
            "default": "Groups",
            "values": [
              "Article Comments",
              "Post Comments",
              "Requests Comments",
              "Ticket Comments",
              "Groups",
              "Organizations",
              "Satisfaction Ratings",
              "Tags",
              "Ticket Fields",
              "Ticket Metrics",
              "Ticket Metric Events",
              "Tickets",
              "Users"
            ]
          }
        },
        {
          "widget-type": "hidden",
          "name": "objectsToSkip",
          "widget-attributes": {"default": ""}
        },
        {
          "widget-type": "textbox",
          "name": "startDate",
          "label": "Start Date",
          "widget-attributes": {"placeholder": "YYYY-MM-DDThh:mm:ssZ (ex: 2019-01-01T00:00:00Z)"}
        },
        {
          "widget-type": "textbox",
          "name": "endDate",
          "label": "End Date",
          "widget-attributes": {"placeholder": "YYYY-MM-DDThh:mm:ssZ (ex: 2019-01-01T00:00:00Z)"},
          "plugin-function": {
            "widget": "outputSchema",
            "output-property": "schema",
            "omit-properties": [{"name": "schema"}],
            "label": "Get Schema"
          }
        },
        {
          "name": "satisfactionRatingsScore",
          "widget-type": "select",
          "label": "Satisfaction Ratings Score",
          "widget-attributes": {
            "default": "",
            "values": [
              "",
              "Offered",
              "Unoffered",
              "Received",
              "Received With Comment",
              "Received Without Comment",
              "Good",
              "Good With Comment",
              "Good Without Comment",
              "Bad",
              "Bad With Comment",
              "Bad Without Comment"
            ]
          }
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "widget-type": "number",
          "name": "maxRetryCount",
          "label": "Max Retry Count",
          "widget-attributes": {
            "default": "20",
            "min": "0"
          }
        },
        {
          "widget-type": "number",
          "name": "maxRetryWait",
          "label": "Max Retry Wait",
          "widget-attributes": {
            "default": "240",
            "min": "0"
          }
        },
        {
          "widget-type": "number",
          "name": "maxRetryJitterWait",
          "label": "Max Retry Jitter Wait",
          "widget-attributes": {
            "default": "100",
            "min": "0"
          }
        },
        {
          "widget-type": "number",
          "name": "connectTimeout",
          "label": "Connect Timeout",
          "widget-attributes": {
            "default": "300",
            "min": "0"
          }
        },
        {
          "widget-type": "number",
          "name": "readTimeout",
          "label": "Read Timeout",
          "widget-attributes": {
            "default": "300",
            "min": "0"
          }
        },
        {
          "widget-type": "hidden",
          "name": "zendeskBaseUrl",
          "widget-attributes": {"default": "https://%s.zendesk.com/api/v2/%s"}
        }
      ]
    }
  ],
